import {useState} from 'react';
import './App.css';

function App() {
  const [playerTag, setPlayerTag] = useState("");
  const COC_BASE_API_URL = "https://api.clashofclans.com/v1";
  return (
    <div>
      <h1> Clash of Clans Player Search </h1>
      <div>
        <label> Player Tag </label><br/>
        <input type="text" id="player-name" name="player-name" onChange={e => setPlayerTag(e.target.value)}/><br/>
        <button onClick={search}> Search </button>
      </div>

      <p> You have entered {playerTag} </p>

      <div id="pokemon-result">This will show the result</div>
    </div>
  );

  function search(){
      alert("Search button has been clicked!");
  }
}

export default App;
