import axios from "axios";
/*import clashApi from 'clash-of-clans-api';*/
import {useState} from 'react';
import './App.css';

function App() {
  const [playerTag, setPlayerTag] = useState("");
  /*const clashApi = require('clash-of-clans-api')*/
  const COC_API_TOKEN = "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiIsImtpZCI6IjI4YTMxOGY3LTAwMDAtYTFlYi03ZmExLTJjNzQzM2M2Y2NhNSJ9..vDUeh-XkAlK9DxtGJNBkPdmtfo0CEDbu6B8bOmSEtdGw4Cz8hthReRrMUyseXKySMEmK9n24Wchbs_rSnDZo8g"
  const config = {
    headers: {
      "Accept": "application/json",
      "Authorization": "Bearer " + COC_API_TOKEN
    }
  }
  /*let client = clashApi({
    token: COC_API_TOKEN
  });*/
  const COC_BASE_API_URL = "https://api.clashofclans.com/v1";
  return (
    <div>
      <h1> Clash of Clans Player Search </h1>
      <div>
        <label> Player Tag </label><br/>
        <input type="text" id="player-tag" name="player-tag" onChange={e => setPlayerTag(e.target.value)}/><br/>
        <button onClick={search}> Search </button>
      </div>

      <p> You have entered {playerTag} </p>

      <div id="player-result">This will show the result</div>
    </div>
  );
  function search() {
    axios({
      method: 'get', 
      url: COC_BASE_API_URL + "/players/#" + playerTag,
      headers: {
        Authorization: 'Bearer ' + COC_API_TOKEN
      }
    }).then((res) => {
      console.log(res.data);
    }).catch((err) => {
      console.error(err);
    });
  }
  /*
  function search() {
    axios.get(COC_BASE_API_URL + "/players/#" + playerTag, {
      headers: {
        "Accept": "application/json",
        "Authorization": "Bearer " + COC_API_TOKEN
      }
    }).then((res) => {
      console.log(res.data);
    }).catch((err) => {
      console.error(err);
    });
  }
  */
}

export default App;
